mapper.insert:
MERGE INTO SHOP_CART
USING DUAL
ON (ITEM_CODE = #{itemCode} AND MEMBER_ID = #{memberId})
WHEN MATCHED THEN
    UPDATE
    SET CART_CNT = CART_CNT + #{cartCnt}
WHEN NOT MATCHED THEN
    INSERT (
        CART_CODE
        , ITEM_CODE
        , CART_CNT
        , MEMBER_ID
    ) VALUES (
        (SELECT 'CART_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CART_CODE, 6))), 0) + 1, 3, '0')
        FROM SHOP_CART)
        , #{itemCode}
        , #{cartCnt}
        , #{memberId}
    )

mapper.update:
UPDATE SHOP_CART
SET
    CART_CNT = #{cartCnt}
WHERE CART_CODE = #{cartCode}

mapper.select:
SELECT C.ITEM_CODE , CART_CNT AS BUY_CNT , ITEM_PRICE * CART_CNT AS BUY_PRICE FROM SHOP_CART C, SHOP_ITEM I WHERE CART_CODE IN
#{cartCode}
AND C.ITEM_CODE = I.ITEM_CODE
